const button=document.querySelector(".main_content_button"),close=document.querySelector(".modal_close"),overlay=document.querySelector(".overlay"),modal=document.querySelector(".modal"),contentCounter=document.querySelector(".modal_paragraph_counter");let counter=0;const showModal=(e,o,t)=>{e.addEventListener("click",e=>{e.preventDefault(),o.classList.add("show"),t.classList.add("show"),counter++,contentCounter.textContent=counter+" times"})},closeModal=(e,o,t)=>{e.addEventListener("click",e=>{e.preventDefault(),o.classList.remove("show"),t.classList.remove("show")})};showModal(button,modal,overlay),closeModal(close,modal,overlay);